COMMENT ON COLUMN "forms"."slug" IS 'Enables embedding a form in multiple pages';

COMMENT ON COLUMN "questions"."value" IS 'The actual question';

COMMENT ON COLUMN "answers"."value" IS 'The actual answer';

COMMENT ON COLUMN "patients"."clinical_history" IS 'Optional patient history such as previous illnesses, allergies, smoking, alcohol or other patient info';

COMMENT ON COLUMN "consultations"."tests" IS 'Medical examinations necessary to diagnose a patient';

COMMENT ON COLUMN "shipments"."country_code" IS 'ISO-3166 alpha-2 recommended';

COMMENT ON COLUMN "payments"."transaction_id" IS 'Foreign transaction ID from a third party service';

